// Get all input items
const inputItems = $input.all();

// Build the array of objects
const groupedArray = inputItems.map(item => {
  return {
    link: item.json.fields?.Lien || null,
    weblink: item.json.webViewLink || null,
  };
});

// Return a single item with the array
return [{
  json: {
    groupedLinks: groupedArray
  }
}];

