// Transformer les donnÃ©es Apify en format structurÃ©
const projectName = $('âš™ï¸ Extraire Configuration').first().json.project_name;
const items = $input.all();

const transcripts = [];
const thumbnails = [];
const keywords = new Set();

for (const item of items) {
  const data = item.json;

  if (data.transcript) {
    transcripts.push({
      video_url: data.url,
      title: data.title,
      transcript: data.transcript,
      duration: data.duration,
      views: data.viewCount || 0,
      likes: data.likeCount || 0
    });
  }

  if (data.thumbnails && data.thumbnails.length > 0) {
    thumbnails.push({
      thumbnail_url: data.thumbnails[0].url,
      video_title: data.title,
      views: data.viewCount || 0,
      ctr_estimated: data.viewCount > 0 ? Math.round((data.likeCount / data.viewCount) * 100 * 100) / 100 : 0
    });
  }

  if (data.keywords) {
    data.keywords.forEach(kw => keywords.add(kw));
  }
}

const summary = {
  total_videos_analyzed: items.length,
  transcripts_count: transcripts.length,
  thumbnails_count: thumbnails.length,
  keywords_count: keywords.size,
  avg_duration: transcripts.length > 0 ? transcripts.reduce((sum, t) => sum + t.duration, 0) / transcripts.length : 0,
  avg_views: transcripts.length > 0 ? transcripts.reduce((sum, t) => sum + t.views, 0) / transcripts.length : 0
};

return [
  {
    json: {
      project_name: projectName,
      transcripts: transcripts,
      thumbnails: thumbnails,
      keywords: Array.from(keywords).map(kw => ({
        keyword: kw,
        search_volume: 0,
        competition: 'unknown'
      })),
      summary: summary
    }
  }
];

// PrÃ©parer les transcripts pour Google Sheets
const data = $input.first().json;
const transcripts = data.transcripts;

return transcripts.map(t => ({ json: t }));

