// Formatter et enrichir la sortie de l'agent
const agentOutput = $input.first().json;
let result;

try {
  if (typeof agentOutput.output === 'string') {
    let clean = agentOutput.output
      .replace(/```json\n?/g, '')
      .replace(/```\n?/g, '')
      .trim();
    result = JSON.parse(clean);
  } else {
    result = agentOutput.output || agentOutput;
  }
} catch(e) {
  result = { 
    status: 'success', 
    data: agentOutput,
    parsing_error: e.message
  };
}

// Enrichir avec mÃ©tadonnÃ©es
result.ai_enhanced = true;
result.optimization_applied = true;
result.scene_format = 'structured';
result.ready_for_production = true;

// Ajouter un mapping pour la phase suivante
if (result.scenes && Array.isArray(result.scenes)) {
  result.scenes_for_visuals = result.scenes.map(scene => ({
    scene_id: scene.scene_id,
    visual_prompt: scene.visual_prompt_en,
    visual_type: $input.first().json.body?.visual_type || 'ai_generation',
    duration: scene.duration_seconds
  }));
}

return [{ json: result }];
