    }
  }];
}

// Calculer la dur√©e totale
const totalDuration = scriptData.script.sections.reduce((sum, section) => sum + (section.duration || 0), 0);

// Cr√©er le script complet en texte
const fullScriptText = `${scriptData.script.introduction}\n\n${scriptData.script.sections.map(s => s.content).join('\n\n')}\n\n${scriptData.script.conclusion}`;

