// D√©couper le script en s√©quences de 5-10 secondes
const script = $json.script;
const sequences = [];
let sequenceId = 1;

// Fonction pour cr√©er des s√©quences
function createSequences(content, sectionTitle, targetDuration = 8) {
  // D√©couper par phrases
  const sentences = content.match(/[^.!?]+[.!?]+/g) || [content];
  let currentSequence = '';
  let currentDuration = 0;

  sentences.forEach((sentence, index) => {
    const sentenceDuration = Math.ceil(sentence.split(' ').length / 2); // ~2 mots par seconde

    if (currentDuration + sentenceDuration > targetDuration && currentSequence) {
      sequences.push({
        id: sequenceId++,
        section: sectionTitle,
        text: currentSequence.trim(),
        duration: currentDuration,
        type: 'text'
      });
      currentSequence = sentence;
      currentDuration = sentenceDuration;
    } else {
      currentSequence += ' ' + sentence;
      currentDuration += sentenceDuration;
    }

    // Derni√®re phrase
    if (index === sentences.length - 1 && currentSequence) {
      sequences.push({
        id: sequenceId++,
        section: sectionTitle,
        text: currentSequence.trim(),
        duration: currentDuration,
        type: 'text'
      });
    }
  });
}

// Introduction
if (script.introduction) {
  createSequences(script.introduction, 'Introduction', 10);
}

// Sections principales
if (script.sections) {
  script.sections.forEach(section => {
    createSequences(section.content, section.title, 8);
  });
}

// Conclusion
if (script.conclusion) {
  createSequences(script.conclusion, 'Conclusion', 10);
}

return sequences.map(seq => ({ json: seq }));

